mapper.select:
SELECT *
FROM restaurant_zzim z
         LEFT JOIN restaurant_info Using (restaurant_seq)
WHERE z.restaurant_seq = #{restaurant_seq}
  AND z.member_seq = #{member_seq}

mapper.update:
UPDATE restaurant_zzim
SET comment = #{comment}
WHERE zzim_seq = #{zzim_seq}

mapper.select:
INSERT INTO restaurant_info(member_seq
                           ,category_seq
                           , restaurant_name
                           , tag
                           , location
                           , description
                           , phone
                           , zipcode
                           , address1
                           , address2
                           , openHour
                           , closeHour
                           , closedDay)
VALUES (#{member_seq}
       ,#{category_seq}
       ,#{restaurant_name}
       ,#{tag}
       ,#{location}
       ,#{description}
       ,#{phone}
       ,#{zipcode}
       ,#{address1}
       ,#{address2}
       ,#{openHour}
       ,#{closeHour}
       ,#{closedDay})
